function updateLinks(t, e) {
  const o = document.querySelectorAll('a');
  o.forEach((o) => {
    let h = o.getAttribute('href');
    h === t &&
      ((h = o.setAttribute('href', e)), (o.textContent = 'The href value has been changed!'));
  });
}
updateLinks('https://www.google.com/', 'https://www.w3schools.com/');
