const celsius=document.querySelector("#celsius-input"),fahrenheit=document.querySelector("#fahrenheit-input"),kelvin=document.querySelector("#kelvin-input"),inputs=document.querySelectorAll(".temp-input");inputs.forEach(e=>{e.addEventListener("input",()=>{"celsius-input"===e.id?(fahrenheit.value=(1.8*parseInt(celsius.value,10)+32).toFixed(2),kelvin.value=(parseInt(celsius.value,10)+273.15).toFixed(2)):"fahrenheit-input"===e.id?(celsius.value=((parseInt(fahrenheit.value,10)-32)/1.8).toFixed(2),kelvin.value=((parseInt(fahrenheit.value,10)-32)/1.8+273.15).toFixed(2)):"kelvin-input"===e.id&&(celsius.value=(parseInt(kelvin.value,10)-273.15).toFixed(2),fahrenheit.value=(1.8*(parseInt(kelvin.value,10)-273.15)+32).toFixed(2))})});